package com.example.musep50.data

import android.content.Context
import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase
import com.example.musep50.data.dao.*
import com.example.musep50.data.entities.*

@Database(
    entities = [
        User::class,
        Operation::class,
        Paiement::class,
        Payer::class
    ],
    version = 2,
    exportSchema = false
)
abstract class AppDatabase : RoomDatabase() {
    abstract fun userDao(): UserDao
    abstract fun operationDao(): OperationDao
    abstract fun paiementDao(): PaiementDao
    abstract fun payerDao(): PayerDao

    companion object {
        @Volatile
        private var INSTANCE: AppDatabase? = null

        fun getDatabase(context: Context): AppDatabase {
            return INSTANCE ?: synchronized(this) {
                val instance = Room.databaseBuilder(
                    context.applicationContext,
                    AppDatabase::class.java,
                    "musep50_database"
                )
                    .fallbackToDestructiveMigration()
                    .build()
                INSTANCE = instance
                instance
            }
        }
    }
}

package com.example.musep50.ui

import android.content.Intent
import android.os.Bundle
import android.view.Menu
import android.view.MenuItem
import android.view.View
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.ViewModelProvider
import androidx.core.widget.doOnTextChanged
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.musep50.R
import com.example.musep50.data.entities.Operation
import com.example.musep50.databinding.ActivityDashboardBinding
import com.example.musep50.ui.adapter.OperationAdapter
import com.example.musep50.viewmodel.DashboardViewModel

class DashboardActivity : AppCompatActivity() {
    private lateinit var binding: ActivityDashboardBinding
    private val viewModel: DashboardViewModel by viewModels()
    private lateinit var adapter: OperationAdapter
    private var allOperations = listOf<Operation>()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityDashboardBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setSupportActionBar(binding.toolbar)

        setupRecyclerView()
        setupSearchBar()
        setupFab()
        observeViewModel()
    }

    private fun setupRecyclerView() {
        adapter = OperationAdapter { operation ->
            val intent = Intent(this, OperationDetailsActivity::class.java)
            intent.putExtra("operation_id", operation.id)
            startActivity(intent)
        }

        binding.operationsRecyclerView.layoutManager = LinearLayoutManager(this)
        binding.operationsRecyclerView.adapter = adapter
    }

    private fun setupSearchBar() {
        binding.searchInput.doOnTextChanged { text, _, _, _ ->
            filterOperations(text.toString())
        }
    }

    private fun setupFab() {
        binding.fabNewOperation.setOnClickListener {
            startActivity(Intent(this, NewOperationActivity::class.java))
        }
    }

    private fun filterOperations(query: String) {
        val filtered = if (query.isBlank()) {
            allOperations
        } else {
            allOperations.filter {
                it.nom.contains(query, ignoreCase = true) ||
                it.type.contains(query, ignoreCase = true)
            }
        }
        adapter.submitList(filtered)
        updateEmptyState(filtered.isEmpty())
    }

    private fun updateEmptyState(isEmpty: Boolean) {
        binding.emptyStateLayout.visibility = if (isEmpty) View.VISIBLE else View.GONE
        binding.operationsRecyclerView.visibility = if (isEmpty) View.GONE else View.VISIBLE
    }

    private fun observeViewModel() {
        viewModel.allOperations.observe(this) { operations ->
            allOperations = operations
            adapter.submitList(operations)
            updateEmptyState(operations.isEmpty())

            viewModel.loadOperationStats(operations.map { it.id })
        }

        viewModel.operationStats.observe(this) { stats ->
            adapter.setOperationStats(stats)
        }
    }

    override fun onCreateOptionsMenu(menu: Menu?): Boolean {
        menuInflater.inflate(R.menu.dashboard_menu, menu)
        return true
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.action_profile -> {
                startActivity(Intent(this, ProfileActivity::class.java))
                true
            }
            R.id.action_settings -> {
                // TODO: Open settings
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }
}

package com.example.musep50.ui

import android.content.Intent
import android.os.Bundle
import android.view.View
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import com.example.musep50.R
import com.example.musep50.databinding.ActivityLoginBinding
import com.example.musep50.viewmodel.AuthViewModel
import com.example.musep50.viewmodel.LoginResult

class LoginActivity : AppCompatActivity() {
    private lateinit var binding: ActivityLoginBinding
    private val viewModel: AuthViewModel by viewModels()
    private var currentPin = ""
    private var currentEmail = ""

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityLoginBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupEmailStep()
        setupPinStep()
        observeViewModel()
    }

    private fun setupPinStep() {
        val pinButtons = listOf(
            binding.pinKeypad.btn0,
            binding.pinKeypad.btn1,
            binding.pinKeypad.btn2,
            binding.pinKeypad.btn3,
            binding.pinKeypad.btn4,
            binding.pinKeypad.btn5,
            binding.pinKeypad.btn6,
            binding.pinKeypad.btn7,
            binding.pinKeypad.btn8,
            binding.pinKeypad.btn9
        )

        pinButtons.forEachIndexed { index, button ->
            button.setOnClickListener {
                addPinDigit(index.toString())
            }
        }

        binding.pinKeypad.btnDelete.setOnClickListener {
            removePinDigit()
        }

        binding.pinKeypad.btnBack.setOnClickListener {
            showEmailStep()
        }
    }

    private fun setupEmailStep() {
        binding.continueButton.setOnClickListener {
            val email = binding.emailInput.text.toString()
            if (email.isNotEmpty()) {
                currentEmail = email
                // Vérifier si l'utilisateur existe en tentant la connexion
                showPinStep()
            } else {
                Toast.makeText(this, "Veuillez entrer votre email", Toast.LENGTH_SHORT).show()
            }
        }

        binding.registerButton.setOnClickListener {
            startActivity(Intent(this, RegisterActivity::class.java))
        }
    }

    private fun addPinDigit(digit: String) {
        if (currentPin.length < 4) {
            currentPin += digit
            updatePinDots()

            if (currentPin.length == 4) {
                viewModel.login(currentEmail, currentPin)
            }
        }
    }

    private fun removePinDigit() {
        if (currentPin.isNotEmpty()) {
            currentPin = currentPin.dropLast(1)
            updatePinDots()
        }
    }

    private fun updatePinDots() {
        val dots = listOf(
            binding.pinDot1,
            binding.pinDot2,
            binding.pinDot3,
            binding.pinDot4
        )

        dots.forEachIndexed { index, dot ->
            if (index < currentPin.length) {
                dot.setBackgroundResource(R.drawable.pin_dot_filled)
            } else {
                dot.setBackgroundResource(R.drawable.pin_dot_empty)
            }
        }
    }

    private fun showEmailStep() {
        binding.emailInputLayout.visibility = View.VISIBLE
        binding.pinLayout.visibility = View.GONE
        binding.continueButton.visibility = View.VISIBLE
        binding.registerButton.visibility = View.VISIBLE
        binding.subtitleText.text = "Entrez votre adresse email"
        currentPin = ""
        updatePinDots()
    }

    private fun showPinStep() {
        binding.emailInputLayout.visibility = View.GONE
        binding.pinLayout.visibility = View.VISIBLE
        binding.continueButton.visibility = View.GONE
        binding.registerButton.visibility = View.GONE
        binding.subtitleText.text = "Entrez votre code PIN"
    }

    private fun observeViewModel() {
        viewModel.loginResult.observe(this) { result ->
            when (result) {
                is LoginResult.Success -> {
                    val sharedPreferences = getSharedPreferences("musep50_prefs", MODE_PRIVATE)
                    sharedPreferences.edit().putLong("current_user_id", result.user.id).apply()

                    startActivity(Intent(this, DashboardActivity::class.java))
                    finish()
                }
                is LoginResult.Error -> {
                    currentPin = ""
                    updatePinDots()
                    Toast.makeText(this, result.message, Toast.LENGTH_SHORT).show()
                }
            }
        }
    }
}

package com.example.musep50.ui

import android.app.DatePickerDialog
import android.os.Bundle
import android.widget.ArrayAdapter
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import com.example.musep50.data.entities.Operation
import com.example.musep50.databinding.ActivityNewOperationBinding
import com.example.musep50.viewmodel.OperationViewModel
import java.text.SimpleDateFormat
import java.util.*

class NewOperationActivity : AppCompatActivity() {
    private lateinit var binding: ActivityNewOperationBinding
    private val viewModel: OperationViewModel by viewModels()
    private val calendar = Calendar.getInstance()
    private val dateFormat = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
    private var dateDebut: Date? = null
    private var dateFin: Date? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityNewOperationBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupToolbar()
        setupTypeDropdown()
        setupDatePickers()
        setupCreateButton()
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupTypeDropdown() {
        val types = arrayOf("ADHESION", "COTISATION_EXCEPTIONNELLE", "FONDS_CAISSE")
        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, types)
        binding.typeInput.setAdapter(adapter)
    }

    private fun setupDatePickers() {
        binding.dateDebutInput.setOnClickListener {
            showDatePicker { date ->
                dateDebut = date
                binding.dateDebutInput.setText(dateFormat.format(date))
            }
        }

        binding.dateFinInput.setOnClickListener {
            showDatePicker { date ->
                dateFin = date
                binding.dateFinInput.setText(dateFormat.format(date))
            }
        }
    }

    private fun showDatePicker(onDateSelected: (Date) -> Unit) {
        val year = calendar.get(Calendar.YEAR)
        val month = calendar.get(Calendar.MONTH)
        val day = calendar.get(Calendar.DAY_OF_MONTH)

        DatePickerDialog(this, { _, selectedYear, selectedMonth, selectedDay ->
            calendar.set(selectedYear, selectedMonth, selectedDay)
            onDateSelected(calendar.time)
        }, year, month, day).show()
    }

    private fun setupCreateButton() {
        binding.createButton.setOnClickListener {
            val nom = binding.nomInput.text.toString()
            val type = binding.typeInput.text.toString()
            val montantStr = binding.montantInput.text.toString()
            val montant = montantStr.toDouble()

            if (validateInputs(nom, type, montantStr)) {
                val operation = Operation(
                    nom = nom,
                    type = type,
                    montantCible = montant,
                    dateDebut = dateDebut?.time ?: System.currentTimeMillis(),
                    dateFin = dateFin?.time,
                    statut = "EN_COURS"
                )

                viewModel.insertOperation(operation)
                Toast.makeText(this, "Opération créée avec succès", Toast.LENGTH_SHORT).show()
                finish()
            }
        }
    }

    private fun validateInputs(nom: String, type: String, montantStr: String): Boolean {
        if (nom.isBlank()) {
            binding.nomInputLayout.error = "Le nom est requis"
            return false
        }

        if (type.isBlank()) {
            binding.typeInputLayout.error = "Le type est requis"
            return false
        }

        if (montantStr.isBlank()) {
            binding.montantInputLayout.error = "Le montant est requis"
            return false
        }

        if (dateDebut == null) {
            Toast.makeText(this, "Veuillez sélectionner la date de début", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateFin == null) {
            Toast.makeText(this, "Veuillez sélectionner la date de fin", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateFin!!.before(dateDebut)) {
            Toast.makeText(this, "La date de fin doit être après la date de début", Toast.LENGTH_SHORT).show()
            return false
        }

        return true
    }
}

package com.example.musep50.ui

import android.os.Bundle
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import androidx.core.widget.doOnTextChanged
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.musep50.data.dao.PaiementWithPayer
import com.example.musep50.data.entities.User
import com.example.musep50.databinding.ActivityOperationDetailsBinding
import com.example.musep50.ui.adapter.PaymentAdapter
import com.example.musep50.viewmodel.AuthViewModel
import com.example.musep50.viewmodel.DashboardViewModel
import com.example.musep50.viewmodel.PaiementViewModel
import java.text.NumberFormat
import java.text.SimpleDateFormat
import java.util.*

class OperationDetailsActivity : AppCompatActivity() {
    private lateinit var binding: ActivityOperationDetailsBinding
    private val dashboardViewModel: DashboardViewModel by viewModels()
    private val paiementViewModel: PaiementViewModel by viewModels()
    private val authViewModel: AuthViewModel by viewModels()
    private lateinit var adapter: PaymentAdapter
    private var allPayments = listOf<PaiementWithPayer>()
    private var allUsers = listOf<User>()
    private val dateFormat = SimpleDateFormat("d MMM yyyy", Locale.FRANCE)
    private val formatter = NumberFormat.getNumberInstance(Locale.FRANCE)
    private var currentOperationId: Long = -1L

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityOperationDetailsBinding.inflate(layoutInflater)
        setContentView(binding.root)

        currentOperationId = intent.getLongExtra("operation_id", -1L)

        setupToolbar()
        setupRecyclerView()
        setupSearch()
        setupFab()
        observeViewModels()
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupRecyclerView() {
        adapter = PaymentAdapter()
        binding.paymentsRecyclerView.layoutManager = LinearLayoutManager(this)
        binding.paymentsRecyclerView.adapter = adapter
    }

    private fun setupSearch() {
        binding.searchInput.doOnTextChanged { text, _, _, _ ->
            filterPayments(text.toString())
        }
    }

    private fun setupFab() {
        binding.fabAddPayment.setOnClickListener {
            val dialog = AddPaymentDialog(operationId = currentOperationId) {
                dashboardViewModel.loadOperationStats(listOf(currentOperationId))
            }
            dialog.show(supportFragmentManager, AddPaymentDialog.TAG)
        }

        binding.btnPublish.setOnClickListener {
            val intent = android.content.Intent(this, PublishActivity::class.java)
            intent.putExtra("operation_id", currentOperationId)
            startActivity(intent)
        }

        binding.btnRetardataires.setOnClickListener {
            val intent = android.content.Intent(this, RetardatairesActivity::class.java)
            intent.putExtra("operation_id", currentOperationId)
            startActivity(intent)
        }
    }

    private fun filterPayments(query: String) {
        val filtered = if (query.isBlank()) {
            allPayments
        } else {
            allPayments.filter { payment ->
                payment.payerName.contains(query, ignoreCase = true) ||
                payment.paiement.methodePaiement.contains(query, ignoreCase = true)
            }
        }
        adapter.submitList(filtered)
    }

    private fun observeViewModels() {
        authViewModel.loginResult.observe(this) { }

        paiementViewModel.getPaiementsWithPayerByOperation(currentOperationId).observe(this) { payments ->
            allPayments = payments
            adapter.submitList(payments)
        }

        dashboardViewModel.allOperations.observe(this) { operations ->
            val operation = operations.find { it.id == currentOperationId }
            operation?.let {
                binding.operationName.text = it.nom
                binding.statusChip.text = it.statut
                binding.operationDates.text = "${dateFormat.format(it.dateDebut)} - ${
                    it.dateFin?.let { date -> dateFormat.format(date) } ?: "N/A"
                }"
                binding.montantCible.text = "${formatter.format(it.montantCible)} FCFA"

                dashboardViewModel.loadOperationStats(listOf(currentOperationId))
            }
        }

        dashboardViewModel.operationStats.observe(this) { stats ->
            val stat = stats[currentOperationId]
            stat?.let {
                binding.montantCollecte.text = "${formatter.format(it.montantCollecte)} FCFA"
                binding.montantRestant.text = "${formatter.format(it.montantRestant)} FCFA"
                binding.progressBar.progress = it.pourcentage
                binding.nombrePayeurs.text = "${it.nombrePaiements} payeur${if (it.nombrePaiements > 1) "s" else ""}"
            }
        }
    }

    override fun onResume() {
        super.onResume()
        loadAllUsers()
    }

    private fun loadAllUsers() {
        authViewModel.loginResult.observe(this) { }

        val sharedPrefs = getSharedPreferences("musep50_prefs", MODE_PRIVATE)
        val database = com.example.musep50.data.AppDatabase.getDatabase(this)
        val repository = com.example.musep50.data.Repository(database)

        repository.getAllUsers().observe(this) { users ->
            allUsers = users
        }
    }
}

