class PublishActivity : AppCompatActivity() {
    private lateinit var binding: ActivityPublishBinding
    private val dashboardViewModel: DashboardViewModel by viewModels()
    private val paiementViewModel: PaiementViewModel by viewModels()
    private var currentOperation: Operation? = null
    private var payments = listOf<PaiementWithPayer>()
    private val dateFormat = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
    private val formatter = NumberFormat.getNumberInstance(Locale.FRANCE)

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityPublishBinding.inflate(layoutInflater)
        setContentView(binding.root)

        val operationId = intent.getLongExtra("operation_id", -1L)

        setupToolbar()
        setupButtons()
        loadData(operationId)
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupButtons() {
        binding.btnCopyText.setOnClickListener {
            copyToClipboard()
        }

        binding.btnShareWhatsapp.setOnClickListener {
            shareOnWhatsApp()
        }

        binding.btnShareOther.setOnClickListener {
            shareToOtherApps()
        }

        binding.btnExportCsv.setOnClickListener {
            exportToCsv()
        }
    }

    private fun loadData(operationId: Long) {
        dashboardViewModel.allOperations.observe(this) { operations ->
            currentOperation = operations.find { it.id == operationId }
            updatePreview()
        }

        paiementViewModel.getPaiementsWithPayerByOperation(operationId).observe(this) { paymentsData ->
            payments = paymentsData
            updatePreview()
        }
    }

    private fun updatePreview() {
        val operation = currentOperation ?: return
        val text = generateFormattedList(operation, payments)
        binding.previewText.text = text
    }

    private fun generateFormattedList(operation: Operation, payments: List<PaiementWithPayer>): String {
        val builder = StringBuilder()

        builder.appendLine("═══════════════════════════")
        builder.appendLine("📋 MUSEP50 - ${operation.nom}")
        builder.appendLine("═══════════════════════════")
        builder.appendLine()
        builder.appendLine("📅 Période: ${dateFormat.format(operation.dateDebut)} - ${operation.dateFin?.let { dateFormat.format(it) } ?: "N/A"}")
        builder.appendLine("💰 Montant ciblé: ${formatter.format(operation.montantCible)} FCFA")
        builder.appendLine()

        val totalCollecte = payments.sumOf { it.paiement.montant }
        val montantRestant = operation.montantCible - totalCollecte

        builder.appendLine("📊 STATISTIQUES")
        builder.appendLine("───────────────────────────")
        builder.appendLine("✅ Montant collecté: ${formatter.format(totalCollecte)} FCFA")
        builder.appendLine("⏳ Montant restant: ${formatter.format(montantRestant)} FCFA")
        builder.appendLine("👥 Nombre de payeurs: ${payments.size}")
        builder.appendLine()

        builder.appendLine("📝 LISTE DES PAIEMENTS")
        builder.appendLine("───────────────────────────")

        if (payments.isEmpty()) {
            builder.appendLine("Aucun paiement enregistré")
        } else {
            payments.forEachIndexed { index, paiementWithUser ->
                builder.appendLine()
                builder.appendLine("${index + 1}. ${paiementWithUser.payerName} - ${formatter.format(paiementWithUser.paiement.montant)} FCFA")
                builder.appendLine("   📅 Date: ${dateFormat.format(paiementWithUser.paiement.datePaiement)}")
                builder.appendLine("   💳 Méthode: ${paiementWithUser.paiement.methodePaiement}")
                if (!paiementWithUser.paiement.commentaire.isNullOrBlank()) {
                    builder.appendLine("   📝 Note: ${paiementWithUser.paiement.commentaire}")
                }
            }
        }

        builder.appendLine()
        builder.appendLine("═══════════════════════════")
        builder.appendLine("Généré le ${dateFormat.format(Date())}")
        builder.appendLine("═══════════════════════════")

        return builder.toString()
    }

    private fun copyToClipboard() {
        val text = binding.previewText.text.toString()
        val clipboard = getSystemService(Context.CLIPBOARD_SERVICE) as ClipboardManager
        val clip = ClipData.newPlainText("Liste de paiements", text)
        clipboard.setPrimaryClip(clip)
        Toast.makeText(this, "Texte copié dans le presse-papiers", Toast.LENGTH_SHORT).show()
    }

    private fun shareOnWhatsApp() {
        val text = binding.previewText.text.toString()
        val sendIntent = Intent().apply {
            action = Intent.ACTION_SEND
            putExtra(Intent.EXTRA_TEXT, text)
            type = "text/plain"
            setPackage("com.whatsapp")
        }

        try {
            startActivity(sendIntent)
        } catch (e: Exception) {
            Toast.makeText(this, "WhatsApp n'est pas installé", Toast.LENGTH_SHORT).show()
            shareToOtherApps()
        }
    }

    private fun shareToOtherApps() {
        val text = binding.previewText.text.toString()
        val sendIntent = Intent().apply {
            action = Intent.ACTION_SEND
            putExtra(Intent.EXTRA_TEXT, text)
            type = "text/plain"
        }
        val shareIntent = Intent.createChooser(sendIntent, "Partager via")
        startActivity(shareIntent)
    }

    private fun exportToCsv() {
        val operation = currentOperation ?: return

        lifecycleScope.launch {
            try {
                val fileName = "musep50_${operation.nom.replace(" ", "_")}_${System.currentTimeMillis()}.csv"
                val file = File(getExternalFilesDir(Environment.DIRECTORY_DOWNLOADS), fileName)

                FileWriter(file).use { writer ->
                    writer.append("Nom du payeur,Contact,Montant (FCFA),Date,Méthode,Commentaire\n")

                    payments.forEach { payment ->
                        writer.append("\"${payment.payerName}\",")
                        val payerInfo = if (payment.payerContact.isNullOrBlank()) {
                            payment.payerName
                        } else {
                            "${payment.payerName} (${payment.payerContact})"
                        }
                        writer.append("\"${payerInfo}\",")
                        writer.append("${payment.paiement.montant},")
                        writer.append("\"${dateFormat.format(payment.paiement.datePaiement)}\",")
                        writer.append("\"${payment.paiement.methodePaiement}\",")
                        writer.append("\"${payment.paiement.commentaire ?: ""}\"\n")
                    }
                }

                Toast.makeText(
                    this@PublishActivity,
                    "CSV exporté: ${file.absolutePath}",
                    Toast.LENGTH_LONG
                ).show()
            } catch (e: Exception) {
                Toast.makeText(
                    this@PublishActivity,
                    "Erreur lors de l'export: ${e.message}",
                    Toast.LENGTH_LONG
                ).show()
            }
        }
    }
}

package com.example.musep50.ui

import android.app.Dialog
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ArrayAdapter
import android.widget.Toast
import androidx.fragment.app.DialogFragment
import androidx.fragment.app.viewModels
import androidx.lifecycle.lifecycleScope
import com.example.musep50.data.entities.Paiement
import com.example.musep50.databinding.DialogAddPaymentBinding
import com.example.musep50.viewmodel.PaiementViewModel
import com.example.musep50.viewmodel.PayerViewModel
import com.google.android.material.dialog.MaterialAlertDialogBuilder

class AddPaymentDialog(
    private val operationId: Long,
    private val onPaymentAdded: () -> Unit
) : DialogFragment() {

    private var _binding: DialogAddPaymentBinding? = null
    private val binding get() = _binding!!
    private val paiementViewModel: PaiementViewModel by viewModels()
    private val payerViewModel: PayerViewModel by viewModels()

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = DialogAddPaymentBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        setupPayerInput()
        setupMethodDropdown()
        setupButtons()
    }

    private fun setupPayerInput() {
        // User can type a new payer name or select from existing
        payerViewModel.getAllPayers().observe(viewLifecycleOwner) { payers ->
            val payerNames = payers.map { it.nom }
            val adapter = ArrayAdapter(requireContext(), android.R.layout.simple_dropdown_item_1line, payerNames)
            binding.payerInput.setAdapter(adapter)
        }
    }

    private fun setupMethodDropdown() {
        val methods = arrayOf("Espèces", "Mobile Money", "Virement", "Autre")
        val adapter = ArrayAdapter(requireContext(), android.R.layout.simple_dropdown_item_1line, methods)
        binding.methodInput.setAdapter(adapter)
    }

    private fun setupButtons() {
        binding.btnCancel.setOnClickListener {
            dismiss()
        }

        binding.btnSave.setOnClickListener {
            if (validateInputs()) {
                savePaiement()
            }
        }
    }

    private fun validateInputs(): Boolean {
        val payerName = binding.payerInput.text.toString()
        if (payerName.isBlank()) {
            binding.payerInputLayout.error = "Le nom du payeur est requis"
            return false
        }

        val montantStr = binding.montantInput.text.toString()
        if (montantStr.isBlank()) {
            binding.montantInputLayout.error = "Le montant est requis"
            return false
        }

        val method = binding.methodInput.text.toString()
        if (method.isBlank()) {
            binding.methodInputLayout.error = "La méthode de paiement est requise"
            return false
        }

        return true
    }

    private fun savePaiement() {
        val payerName = binding.payerInput.text.toString()
        val montant = binding.montantInput.text.toString().toDouble()
        val method = binding.methodInput.text.toString()
        val commentaire = binding.commentaireInput.text?.toString()

        lifecycleScope.launch {
            // First, check if payer exists or create new one
            val payers = payerViewModel.getAllPayersSync()
            var payerId = payers.find { it.nom.equals(payerName, ignoreCase = true) }?.id

            if (payerId == null) {
                // Create new payer
                val newPayer = com.example.musep50.data.entities.Payer(
                    nom = payerName,
                    contact = null,
                    note = null
                )

                payerId = payerViewModel.insertPayer(newPayer)
            }

            val paiement = Paiement(
                operationId = operationId,
                payerId = payerId,
                montant = montant,
                datePaiement = System.currentTimeMillis(),
                methodePaiement = method,
                commentaire = commentaire,
                statut = "Validé"
            )

            paiementViewModel.insertPaiement(paiement,
                onSuccess = {
                    Toast.makeText(requireContext(), "Paiement enregistré", Toast.LENGTH_SHORT).show()
                    onPaymentAdded()
                    dismiss()
                },
                onError = { error ->
                    Toast.makeText(requireContext(), error, Toast.LENGTH_SHORT).show()
                }
            )
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }

    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {
        return MaterialAlertDialogBuilder(requireContext())
            .setView(binding.root)
            .create()
    }

    companion object {
        const val TAG = "AddPaymentDialog"
    }
}

package com.example.musep50.viewmodel

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.LiveData
import androidx.lifecycle.viewModelScope
import com.example.musep50.data.AppDatabase
import com.example.musep50.data.Repository
import com.example.musep50.data.dao.PaiementWithPayer
import com.example.musep50.data.entities.Paiement
import kotlinx.coroutines.launch

class PaiementViewModel(application: Application) : AndroidViewModel(application) {
    private val repository: Repository = Repository(AppDatabase.getDatabase(application))

    fun getPaiementsWithPayerByOperation(operationId: Long): LiveData<List<com.example.musep50.data.dao.PaiementWithPayer>> {
        return repository.getPaiementsWithPayerByOperation(operationId)
    }

    fun insertPaiement(paiement: Paiement, onSuccess: () -> Unit, onError: (String) -> Unit) {
        viewModelScope.launch {
            try {
                repository.insertPaiement(paiement)
                onSuccess()
            } catch (e: Exception) {
                onError(e.message ?: "Erreur lors de l'ajout du paiement")
            }
        }
    }

    fun updatePaiement(paiement: Paiement) {
        viewModelScope.launch {
            repository.updatePaiement(paiement)
        }
    }

    fun deletePaiement(paiement: Paiement) {
        viewModelScope.launch {
            repository.deletePaiement(paiement)
        }
    }
}

package com.example.musep50.ui.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.musep50.data.entities.User
import com.example.musep50.databinding.ItemRetardataireBinding

class RetardataireAdapter(
    private val onSelectionChanged: (List<User>) -> Unit,
    private val onSendIndividual: (User) -> Unit
) : ListAdapter<User, RetardataireAdapter.RetardataireViewHolder>(RetardataireDiffCallback()) {

    private val selectedUsers = mutableSetOf<Long>()

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RetardataireViewHolder {
        val binding = ItemRetardataireBinding.inflate(
            LayoutInflater.from(parent.context),
            parent,
            false
        )
        return RetardataireViewHolder(binding)
    }

    override fun onBindViewHolder(holder: RetardataireViewHolder, position: Int) {
        holder.bind(getItem(position))
    }

    fun selectAll() {
        selectedUsers.clear()
        currentList.forEach { selectedUsers.add(it.id) }
        notifyDataSetChanged()
        notifySelectionChanged()
    }

    fun clearSelection() {
        selectedUsers.clear()
        notifyDataSetChanged()
        notifySelectionChanged()
    }

    private fun notifySelectionChanged() {
        val selected = currentList.filter { selectedUsers.contains(it.id) }
        onSelectionChanged(selected)
    }

    inner class RetardataireViewHolder(
        private val binding: ItemRetardataireBinding
    ) : RecyclerView.ViewHolder(binding.root) {

        fun bind(user: User) {
            binding.userName.text = user.nom
            binding.userEmail.text = user.email

            if (!user.telephone.isNullOrBlank()) {
                binding.userPhone.visibility = View.VISIBLE
                binding.userPhone.text = "📞 ${user.telephone}"
            } else {
                binding.userPhone.visibility = View.GONE
            }

            binding.checkbox.isChecked = selectedUsers.contains(user.id)
            binding.checkbox.setOnCheckedChangeListener { _, isChecked ->
                if (isChecked) {
                    selectedUsers.add(user.id)
                } else {
                    selectedUsers.remove(user.id)
                }
                notifySelectionChanged()
            }

            binding.btnSendIndividual.setOnClickListener {
                onSendIndividual(user)
            }
        }
    }
}

class RetardataireDiffCallback : DiffUtil.ItemCallback<User>() {
    override fun areItemsTheSame(oldItem: User, newItem: User): Boolean {
        return oldItem.id == newItem.id
    }

    override fun areContentsTheSame(oldItem: User, newItem: User): Boolean {
        return oldItem == newItem
    }
}
