class PublishActivity : AppCompatActivity() {
    private lateinit var binding: ActivityPublishBinding
    private val dashboardViewModel: DashboardViewModel by viewModels()
    private val paiementViewModel: PaiementViewModel by viewModels()
    private var currentOperation: Operation? = null
    private var payments = listOf<PaiementWithPayer>()
    private val dateFormat = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
    private val formatter = NumberFormat.getNumberInstance(Locale.FRANCE)

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityPublishBinding.inflate(layoutInflater)
        setContentView(binding.root)

        val operationId = intent.getLongExtra("operation_id", -1L)

        setupToolbar()
        setupButtons()
        loadData(operationId)
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupButtons() {
        binding.btnCopyText.setOnClickListener {
            copyToClipboard()
        }

        binding.btnShareWhatsapp.setOnClickListener {
            shareOnWhatsApp()
        }

        binding.btnShareOther.setOnClickListener {
            shareToOtherApps()
        }

        binding.btnExportCsv.setOnClickListener {
            exportToCsv()
        }
    }

    private fun loadData(operationId: Long) {
        dashboardViewModel.allOperations.observe(this) { operations ->
            currentOperation = operations.find { it.id == operationId }
            updatePreview()
        }

        paiementViewModel.getPaiementsWithPayerByOperation(operationId).observe(this) { paymentsData ->
            payments = paymentsData
            updatePreview()
        }
    }

    private fun updatePreview() {
        val operation = currentOperation ?: return
        val text = generateFormattedList(operation, payments)
        binding.previewText.text = text
    }

    private fun generateFormattedList(operation: Operation, payments: List<PaiementWithPayer>): String {
        val builder = StringBuilder()

        builder.appendLine("‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê")
        builder.appendLine("üìã MUSEP50 - ${operation.nom}")
        builder.appendLine("‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê")
        builder.appendLine()
        builder.appendLine("üìÖ P√©riode: ${dateFormat.format(operation.dateDebut)} - ${operation.dateFin?.let { dateFormat.format(it) } ?: "N/A"}")
        builder.appendLine("üí∞ Montant cibl√©: ${formatter.format(operation.montantCible)} FCFA")
        builder.appendLine()

        val totalCollecte = payments.sumOf { it.paiement.montant }
        val montantRestant = operation.montantCible - totalCollecte

        builder.appendLine("üìä STATISTIQUES")
        builder.appendLine("‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ")
        builder.appendLine("‚úÖ Montant collect√©: ${formatter.format(totalCollecte)} FCFA")
        builder.appendLine("‚è≥ Montant restant: ${formatter.format(montantRestant)} FCFA")
        builder.appendLine("üë• Nombre de payeurs: ${payments.size}")
        builder.appendLine()

        builder.appendLine("üìù LISTE DES PAIEMENTS")
        builder.appendLine("‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ")

        if (payments.isEmpty()) {
            builder.appendLine("Aucun paiement enregistr√©")
        } else {
            payments.forEachIndexed { index, paiementWithUser ->
                builder.appendLine()
                builder.appendLine("${index + 1}. ${paiementWithUser.payerName} - ${formatter.format(paiementWithUser.paiement.montant)} FCFA")
                builder.appendLine("   üìÖ Date: ${dateFormat.format(paiementWithUser.paiement.datePaiement)}")
                builder.appendLine("   üí≥ M√©thode: ${paiementWithUser.paiement.methodePaiement}")
                if (!paiementWithUser.paiement.commentaire.isNullOrBlank()) {
                    builder.appendLine("   üìù Note: ${paiementWithUser.paiement.commentaire}")
                }
            }
        }

        builder.appendLine()
        builder.appendLine("‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê")
        builder.appendLine("G√©n√©r√© le ${dateFormat.format(Date())}")
        builder.appendLine("‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê")

        return builder.toString()
    }

    private fun copyToClipboard() {
        val text = binding.previewText.text.toString()
        val clipboard = getSystemService(Context.CLIPBOARD_SERVICE) as ClipboardManager
        val clip = ClipData.newPlainText("Liste de paiements", text)
        clipboard.setPrimaryClip(clip)
        Toast.makeText(this, "Texte copi√© dans le presse-papiers", Toast.LENGTH_SHORT).show()
    }

    private fun shareOnWhatsApp() {
        val text = binding.previewText.text.toString()
        val sendIntent = Intent().apply {
            action = Intent.ACTION_SEND
            putExtra(Intent.EXTRA_TEXT, text)
            type = "text/plain"
            setPackage("com.whatsapp")
        }

        try {
            startActivity(sendIntent)
        } catch (e: Exception) {
            Toast.makeText(this, "WhatsApp n'est pas install√©", Toast.LENGTH_SHORT).show()
            shareToOtherApps()
        }
    }

    private fun shareToOtherApps() {
        val text = binding.previewText.text.toString()
        val sendIntent = Intent().apply {
            action = Intent.ACTION_SEND
            putExtra(Intent.EXTRA_TEXT, text)
            type = "text/plain"
        }
        val shareIntent = Intent.createChooser(sendIntent, "Partager via")
        startActivity(shareIntent)
    }

    private fun exportToCsv() {
        val operation = currentOperation ?: return

        lifecycleScope.launch {
            try {
                val fileName = "musep50_${operation.nom.replace(" ", "_")}_${System.currentTimeMillis()}.csv"
                val file = File(getExternalFilesDir(Environment.DIRECTORY_DOWNLOADS), fileName)

                FileWriter(file).use { writer ->
                    writer.append("Nom du payeur,Contact,Montant (FCFA),Date,M√©thode,Commentaire\n")

                    payments.forEach { payment ->
                        writer.append("\"${payment.payerName}\",")
                        val payerInfo = if (payment.payerContact.isNullOrBlank()) {
                            payment.payerName
                        } else {
                            "${payment.payerName} (${payment.payerContact})"
                        }
                        writer.append("\"${payerInfo}\",")
                        writer.append("${payment.paiement.montant},")
                        writer.append("\"${dateFormat.format(payment.paiement.datePaiement)}\",")
                        writer.append("\"${payment.paiement.methodePaiement}\",")
                        writer.append("\"${payment.paiement.commentaire ?: ""}\"\n")
                    }
                }

                Toast.makeText(
                    this@PublishActivity,
                    "CSV export√©: ${file.absolutePath}",
                    Toast.LENGTH_LONG
                ).show()
            } catch (e: Exception) {
                Toast.makeText(
                    this@PublishActivity,
                    "Erreur lors de l'export: ${e.message}",
                    Toast.LENGTH_LONG
                ).show()
            }
        }
    }
}

package com.example.musep50.ui

import android.content.Intent
import android.os.Bundle
import android.view.View
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import com.example.musep50.R
import com.example.musep50.databinding.ActivityLoginBinding
import com.example.musep50.viewmodel.AuthViewModel
import com.example.musep50.viewmodel.LoginResult

class LoginActivity : AppCompatActivity() {
    private lateinit var binding: ActivityLoginBinding
    private val viewModel: AuthViewModel by viewModels()
    private var currentPin = ""
    private var currentEmail = ""

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityLoginBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupEmailStep()
        setupPinStep()
        observeViewModel()
    }

    private fun setupPinStep() {
        val pinButtons = listOf(
            binding.pinKeypad.btn0,
            binding.pinKeypad.btn1,
            binding.pinKeypad.btn2,
            binding.pinKeypad.btn3,
            binding.pinKeypad.btn4,
            binding.pinKeypad.btn5,
            binding.pinKeypad.btn6,
            binding.pinKeypad.btn7,
            binding.pinKeypad.btn8,
            binding.pinKeypad.btn9
        )

        pinButtons.forEachIndexed { index, button ->
            button.setOnClickListener {
                addPinDigit(index.toString())
            }
        }

        binding.pinKeypad.btnDelete.setOnClickListener {
            removePinDigit()
        }

        binding.pinKeypad.btnBack.setOnClickListener {
            showEmailStep()
        }
    }

    private fun setupEmailStep() {
        binding.continueButton.setOnClickListener {
            val email = binding.emailInput.text.toString()
            if (email.isNotEmpty()) {
                currentEmail = email
                // V√©rifier si l'utilisateur existe en tentant la connexion
                showPinStep()
            } else {
                Toast.makeText(this, "Veuillez entrer votre email", Toast.LENGTH_SHORT).show()
            }
        }

        binding.registerButton.setOnClickListener {
            startActivity(Intent(this, RegisterActivity::class.java))
        }
    }

    private fun addPinDigit(digit: String) {
        if (currentPin.length < 4) {
            currentPin += digit
            updatePinDots()

            if (currentPin.length == 4) {
                viewModel.login(currentEmail, currentPin)
            }
        }
    }

    private fun removePinDigit() {
        if (currentPin.isNotEmpty()) {
            currentPin = currentPin.dropLast(1)
            updatePinDots()
        }
    }

    private fun updatePinDots() {
        val dots = listOf(
            binding.pinDot1,
            binding.pinDot2,
            binding.pinDot3,
            binding.pinDot4
        )

        dots.forEachIndexed { index, dot ->
            if (index < currentPin.length) {
                dot.setBackgroundResource(R.drawable.pin_dot_filled)
            } else {
                dot.setBackgroundResource(R.drawable.pin_dot_empty)
            }
        }
    }

    private fun showEmailStep() {
        binding.emailInputLayout.visibility = View.VISIBLE
        binding.pinLayout.visibility = View.GONE
        binding.continueButton.visibility = View.VISIBLE
        binding.registerButton.visibility = View.VISIBLE
        binding.subtitleText.text = "Entrez votre adresse email"
        currentPin = ""
        updatePinDots()
    }

    private fun showPinStep() {
        binding.emailInputLayout.visibility = View.GONE
        binding.pinLayout.visibility = View.VISIBLE
        binding.continueButton.visibility = View.GONE
        binding.registerButton.visibility = View.GONE
        binding.subtitleText.text = "Entrez votre code PIN"
    }

    private fun observeViewModel() {
        viewModel.loginResult.observe(this) { result ->
            when (result) {
                is LoginResult.Success -> {
                    try {
                        Toast.makeText(this, "Connexion r√©ussie", Toast.LENGTH_SHORT).show()
                        val intent = Intent(this, DashboardActivity::class.java).apply {
                            putExtra("user_id", result.user.id)
                            flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
                        }
                        startActivity(intent)
                        finish()
                    } catch (e: Exception) {
                        e.printStackTrace()
                        Toast.makeText(this, "Erreur de navigation: ${e.message}", Toast.LENGTH_LONG).show()
                    }
                }
                is LoginResult.Error -> {
                    Toast.makeText(this, result.message, Toast.LENGTH_SHORT).show()
                    currentPin = ""
                    updatePinDots()
                }
            }
        }
    }
}

package com.example.musep50.ui

import android.content.Intent
import android.os.Bundle
import android.view.Menu
import android.view.MenuItem
import android.view.View
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AlertDialog
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.ViewModelProvider
import androidx.lifecycle.lifecycleScope
import androidx.core.widget.doOnTextChanged
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.musep50.R
import com.example.musep50.data.AppDatabase
import com.example.musep50.data.Repository
import com.example.musep50.data.entities.Event
import com.example.musep50.databinding.ActivityDashboardBinding
import com.example.musep50.ui.adapter.EventAdapter
import com.example.musep50.viewmodel.EventViewModel
import kotlinx.coroutines.launch

class DashboardActivity : AppCompatActivity() {
    private lateinit var binding: ActivityDashboardBinding
    private val viewModel: EventViewModel by viewModels()
    private lateinit var adapter: EventAdapter
    private lateinit var repository: Repository
    private var allEvents = listOf<Event>()
    private var currentUserId: Long = -1L

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        try {
            binding = ActivityDashboardBinding.inflate(layoutInflater)
            setContentView(binding.root)

            currentUserId = intent.getLongExtra("user_id", -1L)

            if (currentUserId == -1L) {
                Toast.makeText(this, "Erreur de connexion", Toast.LENGTH_SHORT).show()
                startActivity(Intent(this, LoginActivity::class.java))
                finish()
                return
            }

            setSupportActionBar(binding.toolbar)

            repository = Repository(AppDatabase.getDatabase(this))

            setupRecyclerView()
            setupSearchBar()
            setupFab()
            observeViewModel()
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(this, "Erreur: ${e.message}", Toast.LENGTH_LONG).show()
            startActivity(Intent(this, LoginActivity::class.java))
            finish()
        }
    }

    private fun setupRecyclerView() {
        adapter = EventAdapter(
            onItemClick = { event ->
                val intent = Intent(this, EventOperationsActivity::class.java)
                intent.putExtra("event_id", event.id)
                startActivity(intent)
            },
            onEditClick = { event ->
                editEvent(event)
            },
            onDeleteClick = { event ->
                confirmDeleteEvent(event)
            }
        )

        binding.eventsRecyclerView.layoutManager = LinearLayoutManager(this)
        binding.eventsRecyclerView.adapter = adapter
    }

    private fun editEvent(event: Event) {
        val intent = Intent(this, EditEventActivity::class.java)
        intent.putExtra("event_id", event.id)
        startActivity(intent)
    }

    private fun confirmDeleteEvent(event: Event) {
        AlertDialog.Builder(this)
            .setTitle("Supprimer l'√©v√©nement")
            .setMessage("√ätes-vous s√ªr de vouloir supprimer \"${event.nom}\" ? Toutes les op√©rations associ√©es seront √©galement supprim√©es.")
            .setPositiveButton("Supprimer") { _, _ ->
                deleteEvent(event)
            }
            .setNegativeButton("Annuler", null)
            .show()
    }

    private fun deleteEvent(event: Event) {
        lifecycleScope.launch {
            repository.deleteEvent(event)
        }
    }

    private fun setupSearchBar() {
        binding.searchInput.doOnTextChanged { text, _, _, _ ->
            filterEvents(text.toString())
        }
    }

    private fun setupFab() {
        binding.fabNewEvent.setOnClickListener {
            startActivity(Intent(this, NewEventActivity::class.java))
        }
    }

    private fun filterEvents(query: String) {
        val filtered = if (query.isBlank()) {
            allEvents
        } else {
            allEvents.filter {
                it.nom.contains(query, ignoreCase = true) ||
                        (it.description?.contains(query, ignoreCase = true) == true)
            }
        }
        adapter.submitList(filtered)
        updateEmptyState(filtered.isEmpty())
    }

    private fun updateEmptyState(isEmpty: Boolean) {
        binding.emptyStateLayout.visibility = if (isEmpty) View.VISIBLE else View.GONE
        binding.eventsRecyclerView.visibility = if (isEmpty) View.GONE else View.VISIBLE
    }

    private fun observeViewModel() {
        viewModel.allEvents.observe(this) { events ->
            allEvents = events
            adapter.submitList(events)
            updateEmptyState(events.isEmpty())
        }
    }

    override fun onCreateOptionsMenu(menu: Menu?): Boolean {
        menuInflater.inflate(R.menu.dashboard_menu, menu)
        return true
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.action_profile -> {
                val intent = Intent(this, ProfileActivity::class.java)
                intent.putExtra("user_id", currentUserId)
                startActivity(intent)
                true
            }
            R.id.action_settings -> {
                // TODO: Open settings
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }
}

