import com.example.musep50.data.entities.Payer
import com.example.musep50.databinding.DialogAddPayerBinding
import com.example.musep50.viewmodel.PayerViewModel
import kotlinx.coroutines.launch

class AddPayerDialog(
    private val eventId: Long,
    private val onPayerAdded: () -> Unit
) : DialogFragment() {

    private var _binding: DialogAddPayerBinding? = null
    private val binding get() = _binding!!
    private val payerViewModel: PayerViewModel by viewModels()

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = DialogAddPayerBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        dialog?.window?.setBackgroundDrawableResource(android.R.color.transparent)

        setupButtons()
    }

    private fun setupButtons() {
        binding.btnCancel.setOnClickListener {
            dismiss()
        }

        binding.btnSave.setOnClickListener {
            if (validateInputs()) {
                savePayer()
            }
        }
    }

    private fun validateInputs(): Boolean {
        val payerName = binding.payerNameInput.text.toString()
        if (payerName.isBlank()) {
            binding.payerNameInputLayout.error = "Le nom du payeur est requis"
            return false
        }
        binding.payerNameInputLayout.error = null
        return true
    }

    private fun savePayer() {
        val payerName = binding.payerNameInput.text.toString()
        val contact = binding.payerContactInput.text?.toString()
        val note = binding.payerNoteInput.text?.toString()

        viewLifecycleOwner.lifecycleScope.launch {
            val newPayer = Payer(
                eventId = eventId,
                nom = payerName,
                contact = contact,
                note = note
            )

            try {
                payerViewModel.insertPayer(newPayer)
                Toast.makeText(requireContext(), "Payeur ajout√© avec succ√®s", Toast.LENGTH_SHORT).show()
                onPayerAdded()
                dismiss()
            } catch (e: Exception) {
                Toast.makeText(requireContext(), "Erreur: ${e.message}", Toast.LENGTH_SHORT).show()
            }
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }

    companion object {
        const val TAG = "AddPayerDialog"
    }
}

package com.example.musep50.viewmodel

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.viewModelScope
import com.example.musep50.data.AppDatabase
import com.example.musep50.data.Repository
import com.example.musep50.data.entities.User
import kotlinx.coroutines.launch

class AuthViewModel(application: Application) : AndroidViewModel(application) {
    private val repository: Repository = Repository(AppDatabase.getDatabase(application))

    private val _loginResult = MutableLiveData<LoginResult>()
    val loginResult: LiveData<LoginResult> = _loginResult

    private val _registerResult = MutableLiveData<RegisterResult>()
    val registerResult: LiveData<RegisterResult> = _registerResult

    private val _currentUser = MutableLiveData<User?>()
    val currentUser: LiveData<User?> = _currentUser

    fun login(email: String, pin: String) {
        viewModelScope.launch {
            val user = repository.getUserByEmail(email)
            if (user != null && user.pin == pin) {
                _loginResult.value = LoginResult.Success(user)
            } else {
                _loginResult.value = LoginResult.Error("Email ou PIN incorrect")
            }
        }
    }

    fun register(nom: String, email: String, telephone: String?, organisation: String?, pin: String) {
        viewModelScope.launch {
            try {
                val existingUser = repository.getUserByEmail(email)
                if (existingUser != null) {
                    _registerResult.value = RegisterResult.Error("Un compte avec cet email existe d√©j√†")
                    return@launch
                }

                val user = User(
                    nom = nom,
                    email = email,
                    telephone = telephone,
                    organisation = organisation,
                    pin = pin
                )
                val userId = repository.insertUser(user)
                val createdUser = repository.getUserById(userId)
                _registerResult.value = RegisterResult.Success(createdUser!!)
            } catch (e: Exception) {
                _registerResult.value = RegisterResult.Error("Erreur lors de la cr√©ation du compte: ${e.message}")
            }
        }
    }

    fun insertUser(user: User, onSuccess: () -> Unit, onError: (String) -> Unit) {
        viewModelScope.launch {
            try {
                repository.insertUser(user)
                onSuccess()
            } catch (e: Exception) {
                onError(e.message ?: "Erreur lors de la cr√©ation de l'utilisateur")
            }
        }
    }

    fun getAllUsers(): LiveData<List<User>> = repository.getAllUsers()

    suspend fun getUserById(id: Long): User? = repository.getUserById(id)

    fun loadUserById(userId: Long) {
        viewModelScope.launch {
            val user = repository.getUserById(userId)
            _currentUser.value = user
        }
    }
}

sealed class LoginResult {
    data class Success(val user: User) : LoginResult()
    data class Error(val message: String) : LoginResult()
}

sealed class RegisterResult {
    data class Success(val user: User) : RegisterResult()
    data class Error(val message: String) : RegisterResult()
}

