package com.example.musep50.ui

import android.app.DatePickerDialog
import android.os.Bundle
import android.widget.ArrayAdapter
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import com.example.musep50.data.entities.Event
import com.example.musep50.databinding.ActivityNewEventBinding
import com.example.musep50.viewmodel.EventViewModel
import kotlinx.coroutines.launch
import java.text.SimpleDateFormat
import java.util.*

class NewEventActivity : AppCompatActivity() {
    private lateinit var binding: ActivityNewEventBinding
    private val viewModel: EventViewModel by viewModels()
    private val calendar = Calendar.getInstance()
    private val dateFormat = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
    private var dateDebut: Date? = null
    private var dateFin: Date? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityNewEventBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupToolbar()
        setupStatusDropdown()
        setupDatePickers()
        setupCreateButton()
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupStatusDropdown() {
        val statusOptions = arrayOf("En cours", "Termin√©", "Planifi√©")
        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, statusOptions)
        binding.statusInput.setAdapter(adapter)
    }

    private fun setupDatePickers() {
        binding.startDateInput.setOnClickListener {
            showDatePicker { date ->
                dateDebut = date
                binding.startDateInput.setText(dateFormat.format(date))
            }
        }

        binding.endDateInput.setOnClickListener {
            showDatePicker { date ->
                dateFin = date
                binding.endDateInput.setText(dateFormat.format(date))
            }
        }
    }

    private fun showDatePicker(onDateSelected: (Date) -> Unit) {
        val datePickerDialog = DatePickerDialog(
            this,
            { _, year, month, dayOfMonth ->
                calendar.set(year, month, dayOfMonth)
                onDateSelected(calendar.time)
            },
            calendar.get(Calendar.YEAR),
            calendar.get(Calendar.MONTH),
            calendar.get(Calendar.DAY_OF_MONTH)
        )
        datePickerDialog.show()
    }

    private fun setupCreateButton() {
        binding.btnCreateEvent.setOnClickListener {
            if (validateInputs()) {
                createEvent()
            }
        }
    }

    private fun createEvent() {
        val nom = binding.nameInput.text.toString()
        val description = binding.descriptionInput.text.toString()
        val status = binding.statusInput.text.toString()

        val event = Event(
            nom = nom,
            description = description.ifEmpty { null },
            dateDebut = dateDebut!!.time,
            dateFin = dateFin?.time,
            statut = status.ifEmpty { "En cours" }
        )

        lifecycleScope.launch {
            try {
                viewModel.insertEvent(event)
                Toast.makeText(this@NewEventActivity, "√âv√©nement cr√©√© avec succ√®s", Toast.LENGTH_SHORT).show()
                finish()
            } catch (e: Exception) {
                Toast.makeText(this@NewEventActivity, "Erreur: ${e.message}", Toast.LENGTH_SHORT).show()
            }
        }
    }

    private fun validateInputs(): Boolean {
        val nom = binding.nameInput.text.toString()

        if (nom.isBlank()) {
            Toast.makeText(this, "Veuillez entrer un nom pour l'√©v√©nement", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateDebut == null) {
            Toast.makeText(this, "Veuillez s√©lectionner la date de d√©but", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateFin != null && dateFin!!.before(dateDebut)) {
            Toast.makeText(this, "La date de fin doit √™tre apr√®s la date de d√©but", Toast.LENGTH_SHORT).show()
            return false
        }

        return true
    }
}

package com.example.musep50.ui.adapter

import android.view.LayoutInflater
import android.view.ViewGroup
import android.widget.PopupMenu
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.musep50.R
import com.example.musep50.data.entities.Event
import com.example.musep50.databinding.ItemEventBinding
import java.text.SimpleDateFormat
import java.util.Date
import java.util.Locale

class EventAdapter(
    private val onItemClick: (Event) -> Unit,
    private val onEditClick: (Event) -> Unit,
    private val onDeleteClick: (Event) -> Unit
) : ListAdapter<Event, EventAdapter.EventViewHolder>(EventDiffCallback()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): EventViewHolder {
        val binding = ItemEventBinding.inflate(
            LayoutInflater.from(parent.context),
            parent,
            false
        )
        return EventViewHolder(binding)
    }

    override fun onBindViewHolder(holder: EventViewHolder, position: Int) {
        holder.bind(getItem(position))
    }

    inner class EventViewHolder(
        private val binding: ItemEventBinding
    ) : RecyclerView.ViewHolder(binding.root) {

        fun bind(event: Event) {
            binding.eventName.text = event.nom
            binding.eventDescription.text = event.description ?: "Aucune description"
            binding.statusChip.text = event.statut

            val dateFormatter = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
            binding.eventStartDate.text = dateFormatter.format(Date(event.dateDebut))

            binding.operationCount.text = "0"

            binding.root.setOnClickListener {
                onItemClick(event)
            }

            binding.btnEventMenu.setOnClickListener {
                showPopupMenu(it, event)
            }
        }

        private fun showPopupMenu(view: android.view.View, event: Event) {
            val popup = PopupMenu(view.context, view)
            popup.menuInflater.inflate(R.menu.event_item_menu, popup.menu)
            popup.setOnMenuItemClickListener { menuItem ->
                when (menuItem.itemId) {
                    R.id.action_edit_event -> {
                        onEditClick(event)
                        true
                    }
                    R.id.action_delete_event -> {
                        onDeleteClick(event)
                        true
                    }
                    else -> false
                }
            }
            popup.show()
        }
    }
}

class EventDiffCallback : DiffUtil.ItemCallback<Event>() {
    override fun areItemsTheSame(oldItem: Event, newItem: Event): Boolean {
        return oldItem.id == newItem.id
    }

    override fun areContentsTheSame(oldItem: Event, newItem: Event): Boolean {
        return oldItem == newItem
    }
}

package com.example.musep50.ui.adapter

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.example.musep50.data.entities.Payer
import com.example.musep50.databinding.ItemParticipantBinding

class ParticipantAdapter(
    private val onDeleteClick: (Payer) -> Unit
) : ListAdapter<Payer, ParticipantAdapter.ParticipantViewHolder>(ParticipantDiffCallback()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ParticipantViewHolder {
        val binding = ItemParticipantBinding.inflate(
            LayoutInflater.from(parent.context),
            parent,
            false
        )
        return ParticipantViewHolder(binding)
    }

    override fun onBindViewHolder(holder: ParticipantViewHolder, position: Int) {
        holder.bind(getItem(position))
    }

    inner class ParticipantViewHolder(
        private val binding: ItemParticipantBinding
    ) : RecyclerView.ViewHolder(binding.root) {

        fun bind(payer: Payer) {
            binding.participantName.text = payer.nom

            if (payer.contact.isNullOrBlank()) {
                binding.participantContact.visibility = View.GONE
            } else {
                binding.participantContact.visibility = View.VISIBLE
                binding.participantContact.text = payer.contact
            }

            binding.btnDeleteParticipant.setOnClickListener {
                onDeleteClick(payer)
            }
        }
    }
}

class ParticipantDiffCallback : DiffUtil.ItemCallback<Payer>() {
    override fun areItemsTheSame(oldItem: Payer, newItem: Payer): Boolean {
        return oldItem.id == newItem.id
    }

    override fun areContentsTheSame(oldItem: Payer, newItem: Payer): Boolean {
        return oldItem == newItem
    }
}

package com.example.musep50.ui

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.fragment.app.DialogFragment
import androidx.fragment.app.viewModels
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.musep50.data.entities.Payer
import com.example.musep50.databinding.DialogManageParticipantsBinding
import com.example.musep50.ui.adapter.ParticipantAdapter
import com.example.musep50.viewmodel.PayerViewModel
import kotlinx.coroutines.launch

class ManageParticipantsDialog(
    private val eventId: Long,
    private val eventName: String
) : DialogFragment() {

    private var _binding: DialogManageParticipantsBinding? = null
    private val binding get() = _binding!!
    private val payerViewModel: PayerViewModel by viewModels()
    private lateinit var adapter: ParticipantAdapter

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = DialogManageParticipantsBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        dialog?.window?.setBackgroundDrawableResource(android.R.color.transparent)
        dialog?.window?.setLayout(
            ViewGroup.LayoutParams.MATCH_PARENT,
            ViewGroup.LayoutParams.WRAP_CONTENT
        )

        binding.eventNameText.text = "√âv√©nement: $eventName"

        setupRecyclerView()
        setupButtons()
        observeParticipants()
    }

    private fun setupRecyclerView() {
        adapter = ParticipantAdapter { payer ->
            deleteParticipant(payer)
        }

        binding.participantsRecyclerView.layoutManager = LinearLayoutManager(requireContext())
        binding.participantsRecyclerView.adapter = adapter
    }

    private fun setupButtons() {
        binding.btnAddParticipant.setOnClickListener {
            if (validateInputs()) {
                addParticipant()
            }
        }

        binding.btnClose.setOnClickListener {
            dismiss()
        }
    }

    private fun observeParticipants() {
        payerViewModel.getPayersByEvent(eventId).observe(viewLifecycleOwner) { participants ->
            if (participants.isEmpty()) {
                binding.participantsRecyclerView.visibility = View.GONE
                binding.emptyStateText.visibility = View.VISIBLE
            } else {
                binding.participantsRecyclerView.visibility = View.VISIBLE
                binding.emptyStateText.visibility = View.GONE
                adapter.submitList(participants)
            }
        }
    }

    private fun validateInputs(): Boolean {
        val participantName = binding.payerNameInput.text.toString()
        if (participantName.isBlank()) {
            binding.payerNameInputLayout.error = "Le nom du participant est requis"
            return false
        }
        binding.payerNameInputLayout.error = null
        return true
    }

    private fun addParticipant() {
        val participantName = binding.payerNameInput.text.toString()
        val contact = binding.payerContactInput.text?.toString()

        viewLifecycleOwner.lifecycleScope.launch {
            val newParticipant = Payer(
                eventId = eventId,
                nom = participantName,
                contact = contact
            )

            try {
                payerViewModel.insertPayer(newParticipant)
                Toast.makeText(requireContext(), "Participant ajout√© avec succ√®s", Toast.LENGTH_SHORT).show()

                binding.payerNameInput.text?.clear()
                binding.payerContactInput.text?.clear()
            } catch (e: Exception) {
                Toast.makeText(requireContext(), "Erreur: ${e.message}", Toast.LENGTH_SHORT).show()
            }
        }
    }

    private fun deleteParticipant(payer: Payer) {
        viewLifecycleOwner.lifecycleScope.launch {
            try {
                payerViewModel.deletePayer(payer)
                Toast.makeText(requireContext(), "Participant supprim√©", Toast.LENGTH_SHORT).show()
            } catch (e: Exception) {
                Toast.makeText(requireContext(), "Erreur lors de la suppression: ${e.message}", Toast.LENGTH_SHORT).show()
            }
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }

    companion object {
        const val TAG = "ManageParticipantsDialog"
    }
}

package com.example.musep50.ui

import android.app.DatePickerDialog
import android.os.Bundle
import android.widget.ArrayAdapter
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import com.example.musep50.data.entities.Operation
import com.example.musep50.databinding.ActivityEditOperationBinding
import com.example.musep50.viewmodel.OperationViewModel
import kotlinx.coroutines.launch
import java.text.SimpleDateFormat
import java.util.*

class EditOperationActivity : AppCompatActivity() {
    private lateinit var binding: ActivityEditOperationBinding
    private val viewModel: OperationViewModel by viewModels()
    private val calendar = Calendar.getInstance()
    private val dateFormat = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
    private var dateDebut: Date? = null
    private var dateFin: Date? = null
    private var operationId: Long = -1
    private var eventId: Long = -1
    private var currentOperation: Operation? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityEditOperationBinding.inflate(layoutInflater)
        setContentView(binding.root)

        operationId = intent.getLongExtra("operation_id", -1)
        eventId = intent.getLongExtra("event_id", -1)

        if (operationId == -1L || eventId == -1L) {
            Toast.makeText(this, "Erreur: Donn√©es invalides", Toast.LENGTH_SHORT).show()
            finish()
            return
        }

        setupToolbar()
        setupTypeDropdown()
        setupStatutDropdown()
        setupDatePickers()
        setupSaveButton()
        loadOperation()
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupTypeDropdown() {
        val types = arrayOf("ADHESION", "COTISATION_EXCEPTIONNELLE", "FONDS_CAISSE")
        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, types)
        binding.typeInput.setAdapter(adapter)
    }

    private fun setupStatutDropdown() {
        val statuts = arrayOf("En cours", "Termin√©", "Annul√©")
        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, statuts)
        binding.statutInput.setAdapter(adapter)
    }

    private fun setupDatePickers() {
        binding.dateDebutInput.setOnClickListener {
            showDatePicker { date ->
                dateDebut = date
                binding.dateDebutInput.setText(dateFormat.format(date))
            }
        }

        binding.dateFinInput.setOnClickListener {
            showDatePicker { date ->
                dateFin = date
                binding.dateFinInput.setText(dateFormat.format(date))
            }
        }
    }

    private fun showDatePicker(onDateSelected: (Date) -> Unit) {
        val year = calendar.get(Calendar.YEAR)
        val month = calendar.get(Calendar.MONTH)
        val day = calendar.get(Calendar.DAY_OF_MONTH)

        DatePickerDialog(this, { _, selectedYear, selectedMonth, selectedDay ->
            calendar.set(selectedYear, selectedMonth, selectedDay)
            onDateSelected(calendar.time)
        }, year, month, day).show()
    }

    private fun loadOperation() {
        viewModel.getOperationById(operationId).observe(this) { operation ->
            if (operation != null) {
                currentOperation = operation
                populateFields(operation)
            } else {
                Toast.makeText(this@EditOperationActivity, "Op√©ration introuvable", Toast.LENGTH_SHORT).show()
                finish()
            }
        }
    }

    private fun populateFields(operation: Operation) {
        binding.nomInput.setText(operation.nom)
        binding.typeInput.setText(operation.type, false)
        binding.montantInput.setText(operation.montantCible.toString())
        binding.statutInput.setText(operation.statut, false)

        dateDebut = Date(operation.dateDebut)
        binding.dateDebutInput.setText(dateFormat.format(dateDebut!!))

        operation.dateFin?.let {
            dateFin = Date(it)
            binding.dateFinInput.setText(dateFormat.format(dateFin!!))
        }
    }

    private fun setupSaveButton() {
        binding.saveButton.setOnClickListener {
            val nom = binding.nomInput.text.toString()
            val type = binding.typeInput.text.toString()
            val montantStr = binding.montantInput.text.toString()
            val statut = binding.statutInput.text.toString()

            if (validateInputs(nom, type, montantStr)) {
                updateOperation(nom, type, montantStr.toDouble(), statut)
            }
        }
    }

    private fun updateOperation(nom: String, type: String, montant: Double, statut: String) {
        currentOperation?.let { operation ->
            val updatedOperation = operation.copy(
                nom = nom,
                type = type,
                montantCible = montant,
                dateDebut = dateDebut?.time ?: operation.dateDebut,
                dateFin = dateFin?.time,
                statut = statut.ifEmpty { "En cours" }
            )

            lifecycleScope.launch {
                try {
                    viewModel.updateOperation(updatedOperation)
                    Toast.makeText(this@EditOperationActivity, "Op√©ration modifi√©e avec succ√®s", Toast.LENGTH_SHORT).show()
                    finish()
                } catch (e: Exception) {
                    Toast.makeText(this@EditOperationActivity, "Erreur: ${e.message}", Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    private fun validateInputs(nom: String, type: String, montantStr: String): Boolean {
        if (nom.isBlank()) {
            binding.nomInputLayout.error = "Le nom est requis"
            return false
        }

        if (type.isBlank()) {
            binding.typeInputLayout.error = "Le type est requis"
            return false
        }

        if (montantStr.isBlank()) {
            binding.montantInputLayout.error = "Le montant est requis"
            return false
        }

        if (dateDebut == null) {
            Toast.makeText(this, "Veuillez s√©lectionner la date de d√©but", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateFin == null) {
            Toast.makeText(this, "Veuillez s√©lectionner la date de fin", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateFin!!.before(dateDebut)) {
            Toast.makeText(this, "La date de fin doit √™tre apr√®s la date de d√©but", Toast.LENGTH_SHORT).show()
            return false
        }

        return true
    }
}

package com.example.musep50.ui

import android.app.DatePickerDialog
import android.os.Bundle
import android.widget.ArrayAdapter
import android.widget.Toast
import androidx.activity.viewModels
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import com.example.musep50.data.entities.Event
import com.example.musep50.databinding.ActivityEditEventBinding
import com.example.musep50.viewmodel.EventViewModel
import kotlinx.coroutines.launch
import java.text.SimpleDateFormat
import java.util.*

class EditEventActivity : AppCompatActivity() {
    private lateinit var binding: ActivityEditEventBinding
    private val viewModel: EventViewModel by viewModels()
    private val calendar = Calendar.getInstance()
    private val dateFormat = SimpleDateFormat("dd/MM/yyyy", Locale.FRANCE)
    private var dateDebut: Date? = null
    private var dateFin: Date? = null
    private var eventId: Long = -1
    private var currentEvent: Event? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityEditEventBinding.inflate(layoutInflater)
        setContentView(binding.root)

        eventId = intent.getLongExtra("event_id", -1)

        if (eventId == -1L) {
            Toast.makeText(this, "Erreur: Aucun √©v√©nement s√©lectionn√©", Toast.LENGTH_SHORT).show()
            finish()
            return
        }

        setupToolbar()
        setupStatusDropdown()
        setupDatePickers()
        setupSaveButton()
        loadEvent()
    }

    private fun setupToolbar() {
        setSupportActionBar(binding.toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        binding.toolbar.setNavigationOnClickListener {
            finish()
        }
    }

    private fun setupStatusDropdown() {
        val statusOptions = arrayOf("En cours", "Termin√©", "Planifi√©", "Archiv√©")
        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, statusOptions)
        binding.statusInput.setAdapter(adapter)
    }

    private fun setupDatePickers() {
        binding.startDateInput.setOnClickListener {
            showDatePicker { date ->
                dateDebut = date
                binding.startDateInput.setText(dateFormat.format(date))
            }
        }

        binding.endDateInput.setOnClickListener {
            showDatePicker { date ->
                dateFin = date
                binding.endDateInput.setText(dateFormat.format(date))
            }
        }
    }

    private fun showDatePicker(onDateSelected: (Date) -> Unit) {
        val datePickerDialog = DatePickerDialog(
            this,
            { _, year, month, dayOfMonth ->
                calendar.set(year, month, dayOfMonth)
                onDateSelected(calendar.time)
            },
            calendar.get(Calendar.YEAR),
            calendar.get(Calendar.MONTH),
            calendar.get(Calendar.DAY_OF_MONTH)
        )
        datePickerDialog.show()
    }

    private fun loadEvent() {
        viewModel.getEventById(eventId).observe(this) { event ->
            if (event != null) {
                currentEvent = event
                populateFields(event)
            } else {
                Toast.makeText(this@EditEventActivity, "√âv√©nement introuvable", Toast.LENGTH_SHORT).show()
                finish()
            }
        }
    }

    private fun populateFields(event: Event) {
        binding.nameInput.setText(event.nom)
        binding.descriptionInput.setText(event.description ?: "")
        binding.statusInput.setText(event.statut, false)

        dateDebut = Date(event.dateDebut)
        binding.startDateInput.setText(dateFormat.format(dateDebut!!))

        event.dateFin?.let {
            dateFin = Date(it)
            binding.endDateInput.setText(dateFormat.format(dateFin!!))
        }
    }

    private fun setupSaveButton() {
        binding.btnSaveEvent.setOnClickListener {
            if (validateInputs()) {
                updateEvent()
            }
        }
    }

    private fun updateEvent() {
        val nom = binding.nameInput.text.toString()
        val description = binding.descriptionInput.text.toString()
        val status = binding.statusInput.text.toString()

        currentEvent?.let { event ->
            val updatedEvent = event.copy(
                nom = nom,
                description = description.ifEmpty { null },
                dateDebut = dateDebut!!.time,
                dateFin = dateFin?.time,
                statut = status.ifEmpty { "En cours" }
            )

            lifecycleScope.launch {
                try {
                    viewModel.updateEvent(updatedEvent)
                    Toast.makeText(this@EditEventActivity, "√âv√©nement modifi√© avec succ√®s", Toast.LENGTH_SHORT).show()
                    finish()
                } catch (e: Exception) {
                    Toast.makeText(this@EditEventActivity, "Erreur: ${e.message}", Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    private fun validateInputs(): Boolean {
        val nom = binding.nameInput.text.toString()

        if (nom.isBlank()) {
            Toast.makeText(this, "Veuillez entrer un nom pour l'√©v√©nement", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateDebut == null) {
            Toast.makeText(this, "Veuillez s√©lectionner la date de d√©but", Toast.LENGTH_SHORT).show()
            return false
        }

        if (dateFin != null && dateFin!!.before(dateDebut)) {
            Toast.makeText(this, "La date de fin doit √™tre apr√®s la date de d√©but", Toast.LENGTH_SHORT).show()
            return false
        }

        return true
    }
}
